const styles = () => ({
    globalLoading: {
        position: 'fixed',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        zindex: 99,
        background: 'rgba(0, 0, 0, 0.4)',
    },
    icon: {
        position: 'fixed',
        left: 0,
        right: 0,
        marginLeft: 'auto',
        marginRight: 'auto',
        top: '30%',
        width: 200
    }
});

export default styles;
